jQuery(document).ready(function(){var e;jQuery(".upload_button").live("click",function(t){upload_field=jQuery(this).closest("td").find("input.upload_field:first");upload_preview=jQuery(this).closest("td").find("img.upload_preview:first");t.preventDefault();if(e){e.open();return}e=wp.media.frames.file_frame=wp.media({title:jQuery(this).data("uploader_title"),button:{text:jQuery(this).data("uploader_button_text")},multiple:false});e.on("select",function(){attachment=e.state().get("selection").first().toJSON();upload_field.val(attachment.url);upload_preview.attr("src",attachment.url)});e.open()});jQuery(".clear_button").live("click",function(e){jQuery(this).closest("td").find("input.upload_field:first").val("");jQuery(this).closest("td").find("img.upload_preview:first").hide();return false})})